export class InvokeEvent extends Event {
    /** @param  {EventInit & { action?: string, relatedTarget: Element }} init */
    constructor({ action, relatedTarget, ...options }: EventInit & {
        action?: string;
        relatedTarget: Element;
    });
    /** @type {string} */
    action: string;
    /** @type {Element} */
    relatedTarget: Element;
}
